dt-api.example:
var table = $('#example').DataTable( {
	ajax: "data.json"
} );

setInterval( function () {
	table.ajax.reload();
}, 30000 );

var table = $('#example').DataTable( {
	ajax: "data.json"
} );

setInterval( function () {
	table.ajax.reload( null, false ); // user paging is not reset on reload
}, 30000 );

var table = $('#example').DataTable();

table.ajax.reload( function ( json ) {
	$('#myInput').val( json.lastInput );
} );
