dt-api.example:
var table = $('#example').DataTable();

$('#example tbody').on( 'click', 'td', function () {
	alert( 'Clicked on cell in visible column: '+table.cell( this ).index().columnVisible );
} );

var table = $('#example').DataTable();

$('#example tbody').on( 'click', 'td', function () {
	var rowIdx = table
		.cell( this )
		.index().row;

	table
		.rows( rowIdx )
		.nodes()
		.to$()
		.addClass( 'clicked' );
} );
