dt-api.example:
var table = new DataTable('#myTable');

$('#example').on('click', 'tbody td', function () {
	var data = table.cell(this).render('display');

	console.log(data);
});

var table = new DataTable('#myTable');

$('#example').on('click', 'tbody td', function () {
	var data = table.cell(this).render('sort');

	console.log(data);
});
