dt-api.example:
var table = $('#example').DataTable();

// Create the select list and search operation
var select = $('<select />')
	.appendTo(
		table.column( 0 ).footer()
	)
	.on( 'change', function () {
		table
			.column( 0 )
			.search( $(this).val() )
			.draw();
	} );

// Get the search data for the first column and add to the select list
table
	.column( 0 )
	.cache( 'search' )
	.sort()
	.unique()
	.each( function ( d ) {
		select.append( $('<option value="'+d+'">'+d+'</option>') );
	} );
