dt-api.example:
var table = $('#example').DataTable();

alert( 'Column index 0 is '+
	(table.column( 0 ).visible() === true ? 'visible' : 'not visible')
);

var table = $('#example').DataTable();

for ( var i=0 ; i<4 ; i++ ) {
	table.column( i ).visible( false, false );
}
table.columns.adjust().draw( false ); // adjust column sizing and redraw
