dt-api.example:
var table = $('#example').DataTable();

$('#listData').html( 
	table
		.columns( 0 )
		.data()
		.eq( 0 )      // Reduce the 2D array into a 1D array of data
		.sort()       // Sort data alphabetically
		.unique()     // Reduce to unique values
		.join( '<br>' )
);

var table = $('#example').DataTable();

var idx = table
	.columns( '.check' )
	.data()
	.eq( 0 ) // Reduce the 2D array into a 1D array of data
	.indexOf( 'Yes' );

if ( idx === -1 ) {
	alert( 'Yes not found' );
}
else {
	alert( 'Yes was found' );
}
