dt-api.example:
var table = $('#example').DataTable();

table.rows().eq( 0 ).each( function (idx) {
	var row = table.row( idx );

	if ( row.data().grade === 'A' ) {
		row.node().to$().addClass( 'gradeA' );
	}
} );

var table = $('#example').DataTable();

table.rows().eq( 0 ).each( function (idx) {
	table.row( idx ).child.show();
} );
