dt-api.example:
var table = $('#example').DataTable();

var filteredData = table
	.column( 0 )
	.data()
	.filter( function ( value, index ) {
		return value > 20 ? true : false;
	} );

var table = $('#example').DataTable();

var filteredData = table
	.columns( [0, 1] )
	.data()
	.flatten()
	.filter( function ( value, index ) {
		return value > 20 ? true : false;
	} );
