dt-api.example:
var table = $('#myTable').DataTable();

// Will show "Copy to clipboard"
alert(
	table.i18n( 'buttons.copy', 'Copy to clipboard' )
);

var table = $('#myTable').DataTable( {
	language: {
		buttons: {
			copy: "Click to copy"
		}
	}
} );

// Will show "Click to copy"
alert(
	table.i18n( 'buttons.copy', 'Copy to clipboard' )
);

var table = $('#myTable').DataTable();

// Will show "0 rows selected"
alert(
	table.i18n( 'select.rows', {
		_: '%d rows selected',
		1: '1 row selected'
	}, 0 )
);

// Will show "1 row selected"
alert(
	table.i18n( 'select.rows', {
		_: '%d rows selected',
		1: '1 row selected'
	}, 1 )
);

// Will show "4 rows selected"
alert(
	table.i18n( 'select.rows', {
		_: '%d rows selected',
		1: '1 row selected'
	}, 4 )
);

var table = $('#myTable').DataTable( {
	language: {
		select: {
			rows: {
				_: '%d rows selected',
				0: 'Click a row to select',
				1: 'Just one row selected'
			}
		}
	}
} );

// Will show "Click a row to select"
alert(
	table.i18n( 'select.rows', {
		_: '%d rows selected',
		1: '1 row selected'
	}, 0 )
);

// Will show "Just one row selected"
alert(
	table.i18n( 'select.rows', {
		_: '%d rows selected',
		1: '1 row selected'
	}, 1 )
);

// Will show "4 rows selected"
alert(
	table.i18n( 'select.rows', {
		_: '%d rows selected',
		1: '1 row selected'
	}, 4 )
);
