dt-api.example:
var table = $('#example').DataTable();

table.rows().every( function () {
	this.child( 'Row details for row: '+this.index() );
} );

$('#example tbody').on( 'click', 'tr', function () {
	var child = table.row( this ).child;

	if ( child.isShown() ) {
		child.hide();
	}
	else {
		child.show();
	}
} );
