dt-api.example:
var table = $('#example').DataTable();

$('#example tbody').on( 'click', 'tr', function () {
	alert( 'Row index: '+table.row( this ).index() );
} );

var table = $('#example').DataTable();

$('#example tbody').on( 'click', 'tr', function () {
	var idx = table
		.row( this )
		.index();

	table
		.cell( idx, 0 ) // note that you could actually pass in 'this' as the row selector!
		.data( 'Updated' )
		.draw();
} );
