dt-api.example:
var table = $('#example').DataTable();
var tr = $('#example tbody tr:eq(0)');

tr.find('td:eq(0)').html( 'Updated' );
table
	.row( tr )
	.invalidate()
	.draw();

var pupils = [
	new Pupil(),
	new Pupil(),
	new Pupil(),
	new Pupil()
];

// Create table with data set
var table = $('#example').DataTable( {
	data: pupils
} );

// Modify the data in the data set
pupils[0].name( 'Fred Green' );

// Invalidate and redraw
table
	.row( 0 )
	.invalidate()
	.draw();
