dt-api.example:
var table = $('#example').DataTable();

var data = table
	.rows()
	.data();

alert( 'The table has '+data.length+' records' );

var pupils = [
	new Pupil(),
	new Pupil(),
	new Pupil(),
	new Pupil()
];

// Create table with data set
var table = $('#example').DataTable( {
	data: pupils
} );

var rows = table.rows( 0 ).data();

alert( 'Pupil name in the first row is: '+ rows[0].name() );
