dt-option.example:
new DataTable('#myTable', {
	ajax: {
		url: 'data.json',
		data: {
			user_id: 451
		}
	}
});

new DataTable('#myTable', {
	ajax: {
		url: 'data.json',
		data: function (d) {
			d.extra_search = document.querySelector('#extra').value;
		}
	}
});

new DataTable('#myTable', {
	ajax: {
		url: 'data.json',
		data: function (d) {
			return Object.assign({}, d, {
				extra_search: document.querySelector('#extra').value
			});
		}
	}
});
