dt-option.example:
new DataTable('#myTable', {
	columnDefs: [
		{
			targets: 0,
			searchable: false
		}
	]
});

new DataTable('#myTable', {
	columnDefs: [
		{
			targets: [0, 2],
			orderable: false
		}
	]
});

new DataTable('#myTable', {
	columnDefs: [
		{
			targets: 'nosort',
			orderable: false
		}
	]
});

new DataTable('#myTable', {
	columns: [
		null,
		null,
		null,
		{ name: 'action' }
	],
	columnDefs: [
		{
			targets: 'action:name',
			defaultContent: '<button>Run</button>'
		}
	]
});
