dt-option.example:
new DataTable('#myTable', {
	footerCallback: function (tr, data, start, end, display) {
		$(tr)
			.find('th')
			.eq(0)
			.html('Starting index is ' + start);
	}
});

new DataTable('#myTable', {
	footerCallback: function (tr, data, start, end, display) {
		var api = this.api();
		$(api.column(5).footer()).html(
			api
				.column(5)
				.data()
				.reduce(function (a, b) {
					return a + b;
				}, 0)
		);
	}
});
