dt-option.example:
$('#example').dataTable( {
  "infoCallback": function( settings, start, end, max, total, pre ) {
    return start +" to "+ end;
  }
} );

$('#example').dataTable( {
  "infoCallback": function( settings, start, end, max, total, pre ) {
    var api = this.api();
    var pageInfo = api.page.info();

    return 'Page '+ (pageInfo.page+1) +' of '+ pageInfo.pages;
  }
} );
