dt-option.example:
new DataTable('#example', {
	on: {
		draw: () => {
			console.log('Draw event');
		}
	}
});

new DataTable('#example', {
	ajax: '/api/data',
	on: {
		preXhr: (e, s, data) => {
			data.extra = 1;
		}
	}
});

new DataTable('#example', {
	on: {
		order: () => {
			console.log('Order event');
		},
		search: () => {
			console.log('Search event');
		}
	}
});

new DataTable('#example', {
	on: {
		draw: [
			() => {
				// Listener 1
			},
			() => {
				// Listener 2
			}
		]
	}
});
