dt-example.js:
$('#example').DataTable({
	initComplete: function () {
		var api = this.api();

		api.$('td').on('click', function () {
			api.search(this.innerHTML).draw();
		});
	}
});

dt-example.js-vanilla:
new DataTable('#example', {
	initComplete: function () {
		let api = this.api();

		api.on('click', 'tbody td', function () {
			api.search(this.innerHTML).draw();
		});
	}
});
