dt-example.js:
$(document).ready(function () {
	$('#example').DataTable({
		paging: false,
		ordering: false,
		info: false,
	});
});

dt-example.js-vanilla:
document.addEventListener('DOMContentLoaded', function () {
    new DataTable('#example', {
		paging: false,
		ordering: false,
		info: false,
	});
});
