dt-example.js:
$(document).ready(function () {
	$('#example').DataTable({
		columnDefs: [
			{
				targets: 4,
				render: DataTable.render.datetime('d MMM yyyy', 'MMM d, yy', 'en'),
			},
		],
	});
});

dt-example.js-vanilla:
document.addEventListener('DOMContentLoaded', function () {
    new DataTable('#example', {
		columnDefs: [
			{
				targets: 4,
				render: DataTable.render.datetime('d MMM yyyy', 'MMM d, yy', 'en'),
			},
		],
	});
});
