dt-example.js:
$('#example').DataTable({
	language: {
		search: 'In der Tabelle finden',
		url: 'https://cdn.datatables.net/plug-ins/1.11.5/i18n/de-DE.json'
	}
});

dt-example.js-vanilla:
new DataTable('#example', {
	language: {
		search: 'In der Tabelle finden',
		url: 'https://cdn.datatables.net/plug-ins/1.11.5/i18n/de-DE.json'
	}
});
