dt-example.js:
$(document).ready(function () {
	$('#example').DataTable({
		columnDefs: [
			{
				target: 4,
				render: DataTable.render.date(),
			},
			{
				target: 5,
				render: DataTable.render.number(null, null, 0, '$'),
			},
		],
	});
});

dt-example.js-vanilla:
document.addEventListener('DOMContentLoaded', function () {
    new DataTable('#example', {
		columnDefs: [
			{
				target: 4,
				render: DataTable.render.date(),
			},
			{
				target: 5,
				render: DataTable.render.number(null, null, 0, '$'),
			},
		],
	});
});
