dt-example.css:
div.row-class {
	border: 1px solid #dc3545;
	border-radius: 3px;
	padding: 8px;
}

div.cell-class {
	border: 1px solid #198754;
	border-radius: 3px;
	padding: 8px;
}

/* Styling framework integrations - do note that this isn't a practical example!
 * You would use suitable classes for your setup if you need to modify layout classes.
 */
body.dt-example-bootstrap div.row-class > div {
	float: none;
}

body.dt-example-bootstrap4 div.cell-class {
	margin-top: 10px;
	margin-left: 15px;
}

body.dt-example-bulma div.row-class {
	padding: 0;
}
body.dt-example-bulma div.cell-class {
	margin: 20px 12px 8px;
}

body.dt-example-foundation div.cell-class {
	margin-top: 1em;
}

dt-example.js:
$('#example').DataTable({
	layout: {
		top2: {
			rowId: 'row-id',
			rowClass: 'row-class',
			features: [{
				div: {
					text: 'top2 row'
				}
			}]
		},
		top1: {
			id: 'cell-id',
			className: 'cell-class',
			features: [{
				div: {
					text: 'top1 row'
				}
			}]
		}
	}
});

dt-example.js-vanilla:
new DataTable('#example', {
	layout: {
		top2: {
			rowId: 'row-id',
			rowClass: 'row-class',
			features: {
				div: {
					text: 'top2 row'
				}
			}
		},
		top1: {
			id: 'cell-id',
			className: 'cell-class',
			features: [{
				div: {
					text: 'top1 row'
				}
			}]
		}
	}
});
