dt-example.js:
$.fn.dataTable.ext.type.order['salary-grade-pre'] = function (d) {
	switch (d) {
		case 'Low':
			return 1;
		case 'Medium':
			return 2;
		case 'High':
			return 3;
	}
	return 0;
};

$(document).ready(function () {
	$('#example').DataTable({
		columnDefs: [
			{
				type: 'salary-grade',
				targets: -1,
			},
		],
	});
});
