dt-example.js:
$('#example').DataTable({
	ajax: {
		url: 'scripts/post.php',
		type: 'POST'
	},
	columns: [
		{ data: 'first_name' },
		{ data: 'last_name' },
		{ data: 'position' },
		{ data: 'office' },
		{ data: 'start_date' },
		{ data: 'salary' }
	],
	processing: true,
	serverSide: true
});

dt-example.js-vanilla:
new DataTable('#example', {
	ajax: {
		url: 'scripts/post.php',
		type: 'POST'
	},
	columns: [
		{ data: 'first_name' },
		{ data: 'last_name' },
		{ data: 'position' },
		{ data: 'office' },
		{ data: 'start_date' },
		{ data: 'salary' }
	],
	processing: true,
	serverSide: true
});
