dt-example.js:
$(document).ready(function () {
	$('#example').DataTable({
		ajax: 'scripts/server_processing.php',
		processing: true,
		search: {
			return: true,
		},
		serverSide: true,
	});
});

dt-example.js-vanilla:
document.addEventListener('DOMContentLoaded', function () {
    new DataTable('#example', {
		ajax: 'scripts/server_processing.php',
		processing: true,
		search: {
			return: true,
		},
		serverSide: true,
	});
});
